<script>
export default {
  name: 'app-footer',
  props: {
    isReduced: {
      type: Boolean,
      default: false,
    },
  },
  methods: {
    scrollTo(event){
      const targetSelector = event.target.getAttribute('href');
      const targetNode = document.querySelector(targetSelector);

      const offset = -100;

      try{
        window.scrollTo({
          top: window.pageYOffset + targetNode.getBoundingClientRect().top + offset,
          behavior: 'smooth'
        });
      }
      catch(e){
        console.log(`Элемент ${targetSelector} не найден!`);
      }
    },
  },
  computed: {
    currentYear(){
      return new Date().getFullYear();
    }
  }
}
</script>
